 const toggleReminder = (id) => {
   setTasks(tasks.map((task) => task.id === id ? { ...task, reminder:
  !task.reminder} : task ))
 }

  return (
    <div className="container">
      <Header />
      <AddTask onAdd={addTask}/>
      {tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder} /> : (
        'No Tasks To Show'
      )
      }
    </div>
  );
}

export default App;
